import React, { Component } from 'react'
import { Route, NavLink } from 'react-router-dom'
import Detail from './detail'
export default class index extends Component {
  render() {
    const list = [
      { id: 101, title: 'one' },
      { id: 102, title: 'two' },
      { id: 103, title: 'three' },
    ]

    return (
      <div>
        {/* 用params传递参数 */}
        {/* <ul>
          {list.map((item) => {
            return (
              <NavLink key={item.id} className="list-group-item" to={`/home/message/detail/${item.id}/${item.title}`} >{item.title}</NavLink>
            )
          })}
        </ul>
        <Route path="/home/message/detail/:id/:title" component={Detail}></Route> */}

        {/* 用search传递参数 */}
        {/* <ul>
          {list.map((item) => {
            return (
              <NavLink key={item.id} className="list-group-item" to={`/home/message/detail/?id=${item.id}&title=${item.title}`} >{item.title}</NavLink>
            )
          })}
        </ul>
        <Route path="/home/message/detail" component={Detail}></Route> */}

        {/* 用state传递参数 */}
        <ul>
          {list.map((item) => {
            return (
              <NavLink key={item.id} className="list-group-item" to={{ pathname: '/home/message/detail', state: { id: item.id, title: item.title } }} >{item.title}</NavLink>
            )
          })}
        </ul>
        {/* <Route path="/home/message/detail/:id/:title" component={Detail}></Route> */}
      </div>
    )
  }
}
