import { connect } from 'react-redux'
import { countAction } from '../../redux/actions/count'
import React, { Component } from 'react'

class Count extends Component {
  operation = (type, delay = 0) => {
    const { value } = this.num
    this.props.ff(type, value, delay)
  }
  render() {
    // console.log(this.props);
    return (
      <div>
        <h1>结果：{this.props.data}</h1>
        <select ref={c => this.num = c}>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
        </select>
        <button onClick={() => this.operation('+')}>+</button>
        <button onClick={() => this.operation('-')}>-</button>
        <button onClick={() => this.operation('x')}>x</button>
        <button onClick={() => this.operation('/')}>/</button>
        <button onClick={() => this.operation('+', 500)}>delay plus</button>
      </div>
    )
  }
}

export default connect(
  state => ({ data: state.num }),
  dispatch => ({ ff: (type, value, delay) => setTimeout(() => dispatch(countAction(type, parseInt(value))), delay) })
)(Count)
