import './index.css'
import { menuData } from './bar'
import { Link } from 'react-router-dom'
import React, { Component } from 'react'

export default class index extends Component {
  state = {
    selectedTab: 'home',
    hidden: false
  }
  componentDidMount() {
    this.setState({ selectedTab: this.props.page })
  }
  changeTab = (tab) => {
    // this.setState({ selectedTab: tab })
    console.log(this.state);
    console.log(this.props.page);
  }
  render() {
    return (
      <div >
        <ul className="container">
          {
            menuData.map(item => (
              <li key={item.key}>
                <Link to={item.path} onClick={() => this.changeTab(item.key)}>
                  {/* <Link to={item.path} onClick={this.changeTab.bind(this, item.key)}> */}
                  <div className="link_item">
                    <img src={this.state.selectedTab === this.props.page ? item.selectedIcon : item.icon} alt={item.name} className="link-icon" />
                    {item.name}
                  </div>
                </Link>
              </li>
            ))
          }
        </ul>
      </div>
    )
  }
}
