import React, { Component } from 'react'
import './index.css'
export default class index extends Component {
  state = { mouse: false }
  MouseEnter = () => {
    this.setState({ mouse: true })
  }
  MouseLeave = () => {
    this.setState({ mouse: false })
  }
  changeCheck = (id) => {
    const { handleCheck } = this.props
    return (event) => {
      handleCheck(id, event.target.checked)
    }
  }
  handleDelete = (id) => {
    console.log(id);
  }
  render() {
    // console.log(this.props);
    const { id, name, done } = this.props
    const { mouse } = this.state
    return (
      <li style={mouse ? { backgroundColor: '#ddd' } : {}} onMouseEnter={this.MouseEnter} onMouseLeave={this.MouseLeave}>
        <label>
          <input type="checkbox" defaultChecked={done} onChange={this.changeCheck(id)} />
          <span>{name}</span>
        </label>
        <button style={{ display: mouse ? 'block' : 'none' }} onClick={() => { this.handleDelete(id) }} className="btn btn-danger">删除</button>
      </li>
    )
  }
}
