import React, { Component } from 'react'
import './index.css'
export default class Index extends Component {
  handleSelectAll = (event) => {
    this.props.handleSelectAll(event.target.checked)
    // console.log(this.props.handleSelectAll);
  }
  render() {
    const { hobbies } = this.props
    return (

      <div className="todo-footer">
        <label>
          <input type="checkbox" checked={hobbies.find(item => !item.done) === undefined && hobbies.length !== 0} onChange={this.handleSelectAll} />
        </label>
        <span>
          <span>已完成{hobbies.reduce((pre, current) => { return pre + (current.done ? 1 : 0) }, 0)}</span> / 全部{hobbies.length}
        </span>
        <button className="btn btn-danger">清除已完成任务</button>
      </div>

    )
  }
}
