import React, { Component } from 'react'
import { connect } from 'react-redux'
import { perAction } from '../../redux/actions/person'
class Person extends Component {
  add = () => {
    const newPerson = { id: this.props.state.person.length, name: this.uname.value, age: this.uage.value }
    this.props.ff('add', newPerson)

  }
  render() {
    return (
      <div>
        <h2>Person</h2>
        <input type="text" placeholder="name" ref={c => this.uname = c} />
        <input type="text" placeholder="age" ref={c => this.uage = c} />
        <button onClick={this.add}>add</button>
        {this.props.data.person.map((item) => {
          return <h4 key={item.id}>{item.name}--{item.age}</h4>
        })}

      </div>
    )
  }
}
export default connect(
  state => ({ data: state }),
  dispatch => ({ add: (type, data) => dispatch(perAction(type, data)) })
)(Person)
