import './App.css';
import Header from './components/Header'
import List from './components/List'
import Footer from './components/Footer'
import { Component } from 'react';
class App extends Component {
  state = {
    hobbies: [
      { id: 3, name: 'sleep', done: true },
      { id: 2, name: 'play', done: false },
      { id: 1, name: 'eat', done: false },
    ]
  }
  addHobby = (hobby) => {
    const { hobbies } = this.state
    const newHobby = { id: hobbies.length + 1, name: hobby, done: false }
    this.setState({ hobbies: [newHobby, ...hobbies] })
  }
  handleCheck = (id, check) => {
    console.log(id, check);
    const { hobbies } = this.state
    const NewHobby = hobbies.map((item) => {
      if (item.id === id) return { ...item, done: check }
      else return item
    })
    this.setState({ hobbies: NewHobby })
  }
  handleDelete = (id) => {
    console.log(id);
  }
  render() {
    return (
      <div className="todo-container">
        <div className="todo-wrap">
          <Header addHobby={this.addHobby} />
          <List {...this.state} handleCheck={this.handleCheck} />
          <Footer />
        </div>
      </div>
    );
  }
}

export default App;
