import React, { Component } from 'react'
import store from '../../redux/store'
// import { countAction } from '../../redux/count_action'

export default class index extends Component {
  componentDidMount() {
    store.subscribe(() => { this.forceUpdate() })
  }
  operation = (type, delay = 0) => {
    const { value } = this.num
    // setTimeout(() => store.dispatch(countAction(type, parseInt(value))), delay)
    this.props.ff(type, value, delay)
    // setTimeout(() => store.dispatch({ type, data: parseInt(value) }), delay)
  }
  render() {
    // console.log(this.props);
    return (
      <div>
        <h1>结果：{store.getState()}</h1>
        <select ref={c => this.num = c}>
          <option value="1">1</option>
          <option value="2">2</option>
          <option value="3">3</option>
        </select>
        <button onClick={() => this.operation('+')}>+</button>
        <button onClick={() => this.operation('-')}>-</button>
        <button onClick={() => this.operation('x')}>x</button>
        <button onClick={() => this.operation('/')}>/</button>
        <button onClick={() => this.operation('+', 500)}>delay plus</button>
      </div>
    )
  }
}
