import React from 'react'
import style from './index.module.scss'
import { Input, Button } from 'antd'
export default function Index(props) {

  const errmsg = ''
  const email = 'siyuan'
  const [disable, setDisable] = React.useState(false)
  const [show, setShow] = React.useState('Resend')
  const resend = (e) => {
    setDisable(true)
    let time = 5
    const timer = setInterval(() => {
      time--
      if (time <= 0) {
        clearInterval(timer)
      }
      setShow(time + '秒后')
    }, 1000)
    setTimeout(() => {
      setDisable(false)
      setShow('Resend')
    }, 5000)
  }
  const getCode = (code) => {
    console.log(code);
  }
  return (
    <div className={style.service_container}>
      <div className={style.login_content}>
        <div className={style.title}>Enter Verification Code</div>
        <div className={style.subtitle}>Code sent to {email}</div>
        <div className={style.input}>
          <Input type="tel" maxLength={6} placeholder="6 code number"></Input>
          <Button type="primary" onClick={resend} disabled={disable}>{show}</Button>
        </div>
        <div className={style.errmsg}>{errmsg}</div>
      </div>
    </div >
  )
}
