import React, { Component } from 'react'
import './index.css'
export default class index extends Component {
  state = { mouse: false }
  MouseEnter = () => {
    this.setState({ mouse: true })
  }
  MouseLeave = () => {
    this.setState({ mouse: false })
  }
  render() {
    console.log(this.props);
    const { id, name, done } = this.props
    const { mouse } = this.state
    return (
      <li style={mouse ? { backgroundColor: '#666' } : {}} onMouseEnter={this.MouseEnter} onMouseLeave={this.MouseLeave}>
        <label>
          <input type="checkbox" defaultChecked={done} />
          <span>{name}</span>
        </label>
        <button style={{ display: 'block' }} className="btn btn-danger" style={{ display: 'none' }}>删除</button>
      </li>
    )
  }
}
